// See README.txt for information and build instructions.

// [START declaration]
syntax = "proto3";
package msg;
// [END declaration]

// 账号密码注册
message AccountRegister_Quest
{
	string name = 1;	// 账号名
	string password = 2;
}

message AccountRegister_Resp
{
	int32 result = 1;	// 0成功 1用户名已经存在 2包含非法字符 3账号密码太长
}

//账号密码登入
message AccountLogin_Quest
{
	string name = 1;	// 账号名
	string password = 2;
}

message AccountLogin_Resp
{
	int32 result = 1;	// 0成功 1账号或者密码错误 2包含非法字符 3账号密码太长
}


// 账号验证 ------暂时废弃
message AccountCheck_Quest
{
	uint64	account_id = 2;				// account_id
	string	session_key = 3;		// 登陆设备号
}

// 帐号验证回复
message AccountCheck_Resp
{
	int32      port = 2;		// 端口
	string     ip = 3;			// IP
	string     check_code = 4; //校验码
	
	enum eResult
	{
		eNone			= 0;	//
		eSucceed			= 1;	//
		eCheckFail			= 2;	//
		eSysFail			= 3;	//
	}
	
	int32	result = 5;// [default = eSucceed];	
	string account_id = 6;
}

// 请求登入
message PlayerLogin_Quest  
{
	string	account_id = 2;				
	string  check_out_code=3 ; 	//校验码
}

// 请求登入
message PlayerLogin_Resp
{
	enum eResult
	{
		eNone = 0;
		eSucceed		= 1;	// 成功
		eFail			= 2;	// 失败
	}
	
	int32	result = 2;
}

// 玩家登入流程完成 可以接收逻辑消息
message LoginFlowFinish_Resp  
{
	string account_id = 2;		// 服务器内部使用
	uint64 player_guid = 3;	// 服务器内部使用
	
	string time = 4; 			// 服务器时间，单位秒
	string name = 5;			// 玩家昵称
	int32 money = 6;
	int32 score = 7;
	
	repeated int32 join_game_id = 8;		// 玩家已经报名的比赛ID
	
	repeated string history_guid 	= 9;		// 历史比赛guid唯一标识
	repeated string history_time 	= 10;		// 历史时间戳
	repeated int32 history_count 	= 11;		// 历史比赛报名人数
	repeated int32 history_rank 	= 12;		// 历史比赛排名
	repeated string history_first 	= 13;		// 历史第一名名字
	
}


// 客户端ping
message Client_Ping_Quest
{
	uint64	account_id = 2;		// 服务器内部使用		
}

// 客户端ping 服务器回复
message Client_Ping_Resp
{
	uint64 account_id = 2;	 // 服务器内部使用
}

// 游戏逻辑消息

// 当天游戏列表信息
message GameList_Resp
{
	uint64 account_id = 1;// 服务器内部使用
	repeated int32 id = 2;		// 比赛ID
	repeated string info = 3;	// 时间信息
	repeated int32 state = 4;	// 比赛情况(0关闭 1开启报名 2比赛中)
	repeated int32 count = 5;		// 比赛报名人数
	repeated int32 second = 6;		// 倒计时
}

// C->S游戏报名
message GameApply_Quest  
{
	uint64 account_id = 1;	 // 服务器内部使用
	int32 id = 2;
}

// S->C游戏报名回复
message GameApply_Resp  
{
	uint64 account_id = 2;	 // 服务器内部使用
	int32     result = 4;  // 0失败 1成功
	int32 money = 5;		// 剩余钱币
}

// S->C游戏开始回复
message GameStart_Resp  
{
	uint64 account_id = 2;	 // 服务器内部使用
}

// C->S客户端发起游戏失败(操作问题)
message GameLose_Quest  
{
	uint64 account_id = 2;	 // 服务器内部使用
	uint64 role_guid = 4;	// 服务器内部使用
}

// S->C服务端发起游戏失败(网络问题)
message GameLose_Resp  
{
	uint64 account_id = 2;	 // 服务器内部使用
	int32 nRank = 3;		// 失败自己的排名
}

// S->C游戏结束 结果通知
message GameOver_Resp  
{
	uint64 account_id = 2;	 // 服务器内部使用
	repeated string rank_name = 3;	// 比赛排名 昵称
	int32 nRank = 4;		// 胜利自己的排名
	
}

// S->C报名人数变动通知
message GameCountChange_Resp
{
	int32 game_id = 1;		// 比赛ID
	int32 count = 2;		// 处于报名中的比赛表示报名数量
}

// S->C报名人数变动通知
message GameChangeName_Quest
{
	uint64 account_id = 1;	 // 服务器内部使用
	string name = 2;	// 要修改的玩家昵称
}

// S->C报名人数变动通知
message GameChangeName_Resp
{
	uint64 account_id = 1;	 // 服务器内部使用
	int32 result = 2;		// 改名结果0成功 1含有非法字符 2其他失败
}

// S->C比赛中的游戏剩余人数
message GameRemaining_Resp
{
	uint64 account_id = 1;	 // 服务器内部使用
	int32 count = 2;		// 比赛剩余人数
}

// 支付相关
message ChoosePay_Quest
{
	string account_id = 1;	 // 服务器内部使用
	int32 pay_amount = 2;		// 充值数量
}

message ChoosePay_Resp
{
	string account_id = 1;	 // 服务器内部使用
	string order_id = 2;	// 订单ID
	int32 sign = 3;			// 0成功 1失败
	string result = 4;		// 暂不使用
	int32 pay_amount = 5;		// 充值数量
}

message CheckPay_Quest
{
	string account_id = 1;	 // 服务器内部使用
	string payment_id = 2;		// 结帐返回的付款的唯一标识符只为了成功的支付
	string order_id = 3;		// 订单ID
	string signature = 4;		// 结帐返回的签名,这是用来验证付款
}

message CheckPay_Resp
{
	string account_id = 1;	 // 服务器内部使用
	int32 sign = 2;			// 0成功 1失败
	string order_id = 3;		// 订单ID
	int32 money = 4;		// 玩家的钱
}
