// See README.txt for information and build instructions.

// [START declaration]
syntax = "proto3";
package msg;
// [END declaration]



// 账号验证
message AccountCheck_Quest
{
	uint64	igg_id = 2;				// iggid
	string	session_key = 3;		// 登陆设备号
}

// 帐号验证回复
message AccountCheck_Resp
{
	int32      port = 2;		// 端口
	string     ip = 3;			// IP
	string     check_code = 4; //校验码
	
	enum eResult
	{
		eNone			= 0;	//
		eSucceed			= 1;	//
		eCheckFail			= 2;	//
		eSysFail			= 3;	//
	}
	
	eResult	result = 5;// [default = eSucceed];	
}

// 请求登入
message PlayerLogin_Quest  
{
	uint64	igg_id = 2;				
	string  check_out_code=3 ; 	//校验码
}

// 请求登入
message PlayerLogin_Resp
{
	enum eResult
	{
		eNone = 0;
		eSucceed		= 1;	// 成功
		eFail			= 2;	// 失败
	}
	
	eResult	result = 2;
}

// 玩家登入流程完成 可以接收逻辑消息
message LoginFlowFinish_Resp  
{
	uint64 igg_id = 2;	
	uint64 player_guid = 3;
	uint64 role_guid = 4; 		// 如果不存在角色role_guid为0
}



// 玩家登入流程完成 可以接收逻辑消息
message RoleFlowFinish_Resp  
{
	uint64 igg_id = 2;	 
	uint64 player_guid = 3;
	uint64 role_guid = 4;
	
	int64 time = 5; // 服务器时间，单位ms
}

// 客户端ping
message Client_Ping_Quest
{
	uint64	igg_id = 2;				
}

// 客户端ping 服务器回复
message Client_Ping_Resp
{
	uint64 igg_id = 2;	 
}

// 游戏逻辑消息

// 游戏报名
message GameApply_Quest  
{
	uint64 igg_id = 2;	 
	uint64 player_guid = 3;
	int32      type = 4;
}

// 游戏报名回复
message GameApply_Resp  
{
	uint64 igg_id = 2;	 
	uint64 player_guid = 3;
	int32     result = 4;  // 0失败 1成功
}

// S->C游戏开始回复
message GameStart_Resp  
{
	uint64 igg_id = 2;	 
	uint64 player_guid = 3;
}

// 客户端发起游戏失败(操作问题)
message GameLose_Quest  
{
	uint64 igg_id = 2;	 
	uint64 player_guid = 3;
	uint64 role_guid = 4;	
}

// 服务端发起游戏失败(网络问题)
message GameLose_Resp  
{
	uint64 igg_id = 2;	 
	uint64 player_guid = 3;
}

// S->C游戏结束 结果通知
message GameOver_Resp  
{
	uint64 igg_id = 2;	 
	uint64 player_guid = 3;
}
